import {useState, React } from "react";
import { AllCards } from "./ProductCards.js";
import { AllFilters, Sort } from './SortFilter.js';
import _ from 'lodash';


// props.data = the whole PRODUCTS_LIST json (array of objects)
function App(props) {

  // view data set is:
  //console.log(props.data)

  const [isAscending, setIsAscending] = useState(null);

  const handleClick = (event) => {
      let whichButton = event.currentTarget.value
      if (whichButton === "") {
          whichButton = null;
      } else if (whichButton === "1") {
          setIsAscending(true);
      } else {
          setIsAscending(false);
      }
  }

  let asc = _.sortBy(props.data, ['price']);

  if (isAscending === false) {
    _.reverse(asc);
}

  return (
    <div>
      <Sort data={props.data} callback={handleClick}/>
      <AllFilters data={props.data}/>
      <AllCards data={props.data}/>
    </div>
  );
}

export default App;
