import { useState, React } from "react";
import { AllCards } from "./ProductCards.js";
import { SortFilter } from './SortFilter.js';
import _ from 'lodash';


// props.data = the whole PRODUCTS_LIST json (array of objects)
function App(props) {

  // view data set is:
  //console.log(props.data)

  const [order, setOrder] = useState(props.data);
  const [suncare, setSuncare] = useState(false);

  const applySort = (value) => {
    if (value === '') {
      setOrder(props.data);
    } else {
      let newArray = []
      if (value === "1") {
        newArray = _.sortBy(props.data, ['price']);
      } else if (value === "2") {
        newArray = _.reverse(_.sortBy(props.data, ['price']))
      }
      setOrder(newArray);
    }
  }

  const handleSuncare = (event) => {
    let checked = event.target.checked;
    if (checked === true) {
      return setSuncare(true);
    } else {
      return setSuncare(false);
    }
  }

  return (
    <div>
      <SortFilter applySortCallback={applySort} applyFilterCallback={handleSuncare} data={props.data} />
      <AllCards data={order} />
    </div>
  );
}

export default App;
