import { useState, React } from "react";
import { AllCards } from "./ProductCards.js";
import { AllFilters, Sort } from './SortFilter.js';
import _ from 'lodash';


// props.data = the whole PRODUCTS_LIST json (array of objects)
function App(props) {

  // view data set is:
  //console.log(props.data)

  const [order, setOrder] = useState(props.data);

  let asc = _.sortBy(props.data, ['price']);
  let desc = _.reverse(asc)

  const applySort = (value) => {
    if (value === '') {
      setOrder(props.data);
    } else {
      let newArray = []
      if (value === "1") {
        newArray = _.sortBy(props.data, ['price']);
      } else if (value === "2") {
        newArray = _.reverse(asc)
      }
      setOrder(newArray);
    }
  }

  return (
    <div>
      <Sort applyCallback={applySort} />
      <AllFilters data={props.data} />
      <AllCards data={order} />
    </div>
  );
}

export default App;
