import { useState, React } from "react";
import { AllCards } from "./ProductCards.js";
import { SortFilter } from './SortFilter.js';
import _ from 'lodash';


// props.data = the whole PRODUCTS_LIST json (array of objects)
function App(props) {

  // view data set is:
  //console.log(props.data)

  const [order, setOrder] = useState(props.data);

  const applySortFilter = (value, suncareCheck) => {
    if (value === '') {

      let filteredFullData = props.data.filter((item) => {
        if (suncareCheck === true) {
          return item;
        }
      })
      setOrder(filteredFullData);
    } else {
      let newArray = []
      if (value === "1") {
        newArray = _.sortBy(props.data, ['price']);
      } else if (value === "2") {
        newArray = _.reverse(_.sortBy(props.data, ['price']))
      }
      newArray.filter((item) => {
        if (suncareCheck === true) {
          return item;
        }
      })
      setOrder(newArray);
    }
  }

return (
  <div>
    <SortFilter applySortFilterCallback={applySortFilter} data={props.data} />
    <AllCards data={order} />
  </div>
);
}

export default App;
